HpoExperimentConfiguration.example:
{
    "output_config": {
        "model_path": "bi_ps_${exp_id}_${trial_id}"
    },
    "platform_config": {
        "cmd": [
            "PAI -name ps_smart\n    -project foo",
            "PAI -name prediction\n    -project foo",
            "PAI -name evaluate -project foo",
            "INSERT OVERWRITE TABLE my_table;"
        ],
        "name": "MaxCompute"
    },
    "metric_config": {
        "metric_dict": {
            "recall": 0.5,
            "auc": 0.25,
            "precision": 0.25
        },
        "metric_source": [
            "select * from my_metrics where pt='${exp_id}_${trial_id}';"
        ],
        "metric_type": "table",
        "final_mode": "best"
    },
    "odps_config": {
        "project_name": "my_project",
        "role_arn": "acs:ram::123456789:role/aliyunserviceroleforfoo",
        "region": "cn-shanghai",
        "end_point": "http://service.cn.maxcompute.aliyun-inc.com/api",
        "log_view_host": "http://logview.odps.aliyun.com"
    },
    "yml_config": {
        "max_trial_number": 5,
        "assessor": {
            "name": "PAIAssessor",
            "class_args": {
                "earlystop": true,
                "start_step": 5,
                "optimize_mode": "maximize"
            }
        },
        "experiment_name": "my_exp",
        "tuner": {
            "name": "TPE",
            "class_args": {
                "optimize_mode": "maximize"
            }
        },
        "trial_concurrency": 2
    },
    "search_space": {
        "${max_depth}": {
            "_type": "choice",
            "_value": [
                1,
                3,
                5
            ]
        },
        "${tree_count}": {
            "_value": [
                50,
                100,
                150
            ],
            "_type": "choice"
        }
    }
}

